    /**
     * Realiza um de/para dos campos do layout vínculados as avaliações de 2016 para 2017
     * - OBS.: Para campos novos, tem que atualizar de outra forma
     */
    private function deParaLayoutCampo2016ComAvaliacaoPara2017()
    {

        /**
         * Cria uma tabela de de/para os campos do layout
         */
        $this->execute( <<<SQL
drop table if exists w_de_para_campos_layout_censo;
create table w_de_para_campos_layout_censo as
select db52_nome AS campo,
       ed313_db_layoutcampo as layoutcampo_2016,
       null::int as layoutcampo_2017,
       ed313_avaliacaoperguntaopcao,
       ed313_layoutvalorcampo
  FROM avaliacaogrupopergunta
INNER JOIN avaliacaopergunta ON db103_avaliacaogrupopergunta = db102_sequencial
INNER JOIN avaliacaoperguntaopcao ON db104_avaliacaopergunta = db103_sequencial
INNER JOIN avaliacaoperguntaopcaolayoutcampo ON db104_sequencial = ed313_avaliacaoperguntaopcao
INNER JOIN db_layoutcampos ON db52_codigo = ed313_db_layoutcampo
WHERE db102_avaliacao in (3000000, 3000002)
  AND ed313_ano = 2016;
SQL
        );

        /**
         * Identifica o codigo do campo no layout de 2017 e atualiza o campo "layoutcampo_2017 ""
         */
        $this->execute( <<<SQL
update w_de_para_campos_layout_censo set layoutcampo_2017 = db52_codigo
  from db_layoutcampos
  join db_layoutlinha                on db51_codigo = db52_layoutlinha
 where db51_layouttxt = 281
   and db52_nome = campo;
SQL
        );

        /**
         * Atualiza os dados de 2017 com as avaliações
         */
        $this->execute( <<<SQL
insert into avaliacaoperguntaopcaolayoutcampo
select  nextval('avaliacaoperguntaopcaolayoutcampo_ed313_sequencial_seq'),
        2017,
        layoutcampo_2017,
        ed313_avaliacaoperguntaopcao,
        ed313_layoutvalorcampo
 from  w_de_para_campos_layout_censo;
SQL
        );
    }

    /**
     * Realiza uma migração das disciplinas do censo de 2016 para 2017
     */
    private function migraDisciplinasCenso2016Para2017()
    {
        $this->execute( <<<SQL
drop table if exists w_censoregradisc2017;
create table w_censoregradisc2017 as
select nextval('censoregradisc_ed272_i_codigo_seq') as codigo, ed272_i_censoetapa, ed272_i_censodisciplina, 2017::int as ano
  from censoregradisc
 where ed272_ano = 2016
   and not exists (select 1 from censoregradisc as xxx
                    where xxx.ed272_i_censodisciplina = censoregradisc.ed272_i_censodisciplina
                      and xxx.ed272_ano = 2017) ;
SQL
          );

        // inser as disciplinas do censo
        $this->execute( <<<SQL
insert into censoregradisc select codigo, ed272_i_censoetapa, ed272_i_censodisciplina, ano from w_censoregradisc2017;
SQL
        );
    }

