\ECidade\V3\Window\Session::iterateAll(function($name, $id) use ($data) {

  foreach ($data as $key => $value) {

    if (empty($value)) {
      unset($_SESSION[$key]);
      continue;
    }

    $_SESSION[$key] = $value;
  }
});
